body {
    /* remove margins and scroll bars */
    margin: 0;
    overflow: hidden;
  
    /* style text */
    text-align: center;
    font-size: 12px;
    font-family: Sans-Serif;
  
    /* color text */
    color: #444;
  }
  
  h1 {
    /* position the heading */
    position: absolute;
    width: 100%;
  
    /* make sure that the heading is drawn on top */
    z-index: 1;
  }
  
  #scene-container {
    /* tell our scene container to take up the full page */
    position: absolute;
    width: 100%;
    height: 100%;
  
    /*
      Set the container's background color to the same as the scene's
      background to prevent flashing on load
    */
    background-color: #71c5b0;
  }

  .contenedor-boton{
    width: 100%;
    height: 90px; 
      
    background-color: hsl(167, 9%, 19%);
    font-size: xx-large;
  }

  .contenedor-boton button{
    
    width: 100px;
    height: 60px;
    margin: 10px ; 
    border-radius: 20px;
    background-color: transparent;
    border: none;
    color: rgb(255, 0, 170);
    box-shadow: 10px 5px 17px black;
    font-size: large;
  }

  
  
  .btn-off  ::before{
    background-color: hsl(165, 42%, 31%);
    padding: 10% 10%;
    content: 'Start'; 
    
    border-radius: 20px;
  }
  .btn-on  ::before{
    background-color: hsl(51, 86%, 49%);
    color: rgb(13, 42, 49);
    padding: 10% 10%;
    content: 'Stop'; 
    
    border-radius: 20px;
  }
